    void IMG_sobel_3x3_8(Uint8 *in,Uint8 *out,short cols,short rows)
    {
      int H, O, V, i;
      int i00, i01, i02;
      int i10,      i12;
      int i20, i21, i22;
      int w = cols;

      for (i=0; i<cols*(rows-2)-2; i++) {
        i00=in[i    ]; i01=in[i    +1]; i02=in[i    +2];
        i10=in[i+  w];                  i12=in[i+  w+2];
        i20=in[i+2*w]; i21=in[i+2*w+1]; i22=in[i+2*w+2];

        H = -   i00 - 2*i01 -   i02 +
            +   i20 + 2*i21 +   i22;

        V = -   i00         +   i02
            - 2*i10         + 2*i12
            -   i20         +   i22;

        O = abs(H) + abs(V);

        if (O > 255) O = 255;

        out[i + 1] = O;
      }
    }

    void IMG_sobel_3x3_16(Uint16 * in,Uint16 * out,short cols,short rows)
    {
      int H, O, V, i;
      int i00, i01, i02;
      int i10,      i12;
      int i20, i21, i22;
      int w = cols;

      for (i=0; i<cols*(rows-2)-2; i++) {

        i00 = in[i + 0 * w]; i01 = in[i + 0 * w + 1]; i02 = in[i + 0 * w + 2];
        i10 = in[i + 1 * w];                          i12 = in[i + 1 * w + 2];
        i20 = in[i + 2 * w]; i21 = in[i + 2 * w + 1]; i22 = in[i + 2 * w + 2];

        H = -   i00  -  2*i01  -    i02  +
            +   i20  +  2*i21  +    i22;

        V = -   i00            +    i02
            - 2*i10            +  2*i12
            -   i20            +    i22;

        O = abs(H) + abs(V);

        if (O > 65535) O = 65535;

        out[i + 1] = O;
      }
    }

    void IMG_sobel_3x3_16s(short *in,short *out,short cols,short rows)
    {
      int    H,       O,       V;
      int    i;
      int    i00,     i01,     i02;
      int    i10,     i12;
      int    i20,     i21,     i22;

      for (i=0; i<(cols*(rows-2)-2); i++) {

        i00 =   in[i               ];
        i01 =   in[i            + 1];
        i02 =   in[i            + 2];
        i10 =   in[i +     cols    ];
        i12 =   in[i +     cols + 2];
        i20 =   in[i + 2 * cols    ];
        i21 =   in[i + 2 * cols + 1];
        i22 =   in[i + 2 * cols + 2];

        H   =   - i00 - 2 * i01 -  i02
                + i20 + 2 * i21 +  i22;

        V   =   - i00      +     i02
                - 2 * i10  + 2 * i12
                - i20      +     i22;

        O   =   abs(H) + abs(V);


        O   =   (O > 32767) ? 32767 : O;


        out[i + 1] = O;
      }
    }

    void IMG_sobel_5x5_16s(short *in,short *out,short cols,short rows)
    {
        int    H,       O,       V;
        int    i;

        int    i00,     i01,     i02;
        int    i03,     i04,     i10;
        int    i11,     i12,     i13;
        int    i14,     i20,     i21;
        int    i23,     i24,     i30;
        int    i31,     i32,     i33;
        int    i34,     i40,     i41;
        int    i42,     i43,     i44;


        for (i = 0; i < cols * (rows - 4) - 4; i++) {

            i00 = in[i               ];
            i01 = in[i +            1];
            i02 = in[i +            2];
            i03 = in[i +            3];
            i04 = in[i +            4];

            i10 = in[i +     cols    ];
            i11 = in[i +     cols + 1];
            i12 = in[i +     cols + 2];
            i13 = in[i +     cols + 3];
            i14 = in[i +     cols + 4];

            i20 = in[i + 2 * cols    ];
            i21 = in[i + 2 * cols + 1];
            i23 = in[i + 2 * cols + 3];
            i24 = in[i + 2 * cols + 4];

            i30 = in[i + 3 * cols    ];
            i31 = in[i + 3 * cols + 1];
            i32 = in[i + 3 * cols + 2];
            i33 = in[i + 3 * cols + 3];
            i34 = in[i + 3 * cols + 4];

            i40 = in[i + 4 * cols    ];
            i41 = in[i + 4 * cols + 1];
            i42 = in[i + 4 * cols + 2];
            i43 = in[i + 4 * cols + 3];
            i44 = in[i + 4 * cols + 4];

            H = -     i00 - 4 * i01 - 6 * i02 - 4 * i03 -     i04
                - 2 * i10 - 8 * i11 -12 * i12 - 8 * i13 - 2 * i14

                + 2 * i30 + 8 * i31 +12 * i32 + 8 * i33 + 2 * i34
                +     i40 + 4 * i41 + 6 * i42 + 4 * i43 +     i44;

            V = +     i00 + 2 * i01           - 2 * i03 -     i04
                + 4 * i10 + 8 * i11           - 8 * i13 - 4 * i14
                + 6 * i20 +12 * i21           -12 * i23 - 6 * i24
                + 4 * i30 + 8 * i31           - 8 * i33 - 4 * i34
                +     i40 + 2 * i41           - 2 * i43 -     i44;

            O = abs(H) + abs(V);


            O = (O > 32767) ? 32767 : O;

            out[i + 2] = O;
        }
    }

