        i64 = in[i + 6 * cols + 4];
        i65 = in[i + 6 * cols + 5];
        i66 = in[i + 6 * cols + 6];


        H = -     i00 -  i01 - i02 - 2 * i03 - i04 - i05 - i06
            -     i10 -  i11 - i12 - 2 * i13 - i14 - i15 - i16
            -     i20 -  i21 - i22 - 2 * i23 - i24 - i25 - i26

            +     i40 +  i41 + i42 + 2 * i43 + i44 + i45 + i46
            +     i50 +  i51 + i52 + 2 * i53 + i54 + i55 + i56
            +     i60 +  i61 + i62 + 2 * i63 + i64 + i65 + i66;

