            - 2 * i30 - 2 * i31 - 2 * i32    +  2 * i34 + 2 * i35 + 2*i36
            -     i40 -     i41 -     i42    +      i44 +     i45 +   i46
            -     i50 -     i51 -     i52    +      i54 +     i55 +   i56
            -     i60 -     i61 -     i62    +      i64 +     i65 +   i66;

        O = abs(H) + abs(V);

