    Int8 FillConvexPoly( Mat* img,L_Point32* v,Int32 npts,Int32 color,linetype connectivity,Int32 shift)
    {
        struct
        {
            Int32 idx, di;
            Int32 x, dx, ye;
        }
        edge[2];

        Int32 delta = shift ? 1 << (shift - 1) : 0;
        Int32 i, y, imin = 0, left = 0, right = 1, x1, x2;
        Int32 edges = npts;
        Int32 xmin, xmax, ymin, ymax;
        Uint8* ptr = img->data;
        Int32 nHeight=img->nHeight;
        Int32 nWidth=img->nWidth;
        Int32 pix_size = getsizeof(img->_type);
        L_Point32 p0;
        Int32 step=getsizeof(img->_type)*img->nWidth;
        Int32 delta1, delta2;


        delta1 = delta2 = XY_ONE >> 1;

        p0 = v[npts - 1];
        p0.x <<= XY_SHIFT - shift;
        p0.y <<= XY_SHIFT - shift;

        xmin = xmax = v[0].x;
        ymin = ymax = v[0].y;

        for( i = 0; i < npts; i++ )
        {
            L_Point32 p = v[i];
            if( p.y < ymin )
            {
                ymin = p.y;
                imin = i;
            }

            ymax = MAX( ymax, p.y );
            xmax = MAX( xmax, p.x );
            xmin = MIN( xmin, p.x );

            p.x <<= XY_SHIFT - shift;
            p.y <<= XY_SHIFT - shift;

            p0 = p;
        }

        xmin = (xmin + delta) >> shift;
        xmax = (xmax + delta) >> shift;
        ymin = (ymin + delta) >> shift;
        ymax = (ymax + delta) >> shift;

        if( npts < 3 || xmax < 0 || ymax < 0 || xmin >= nWidth || ymin >= nHeight )
            return 0;

        ymax = MIN( ymax, nHeight - 1 );
        edge[0].idx = edge[1].idx = imin;

        edge[0].ye = edge[1].ye = y = ymin;
        edge[0].di = 1;
        edge[1].di = npts - 1;

        ptr += step*y;

        do
        {
            for( i = 0; i < 2; i++ )
            {
                if( y >= edge[i].ye )
                {
                    Int32 idx = edge[i].idx, di = edge[i].di;
                    Int32 xs = 0, xe, ye, ty = 0;

                    for(;;)
                    {
                        ty = (v[idx].y + delta) >> shift;
                        if( ty > y || edges == 0 )
                            break;
                        xs = v[idx].x;
                        idx += di;
                        idx -= ((idx < npts) - 1) & npts;
                        edges--;
                    }

                    ye = ty;
                    xs <<= XY_SHIFT - shift;
                    xe = v[idx].x << (XY_SHIFT - shift);


                    if( y >= ye )
                        return 0;

                    edge[i].ye = ye;
                    edge[i].dx = ((xe - xs)*2 + (ye - y)) / (2 * (ye - y));
                    edge[i].x = xs;
                    edge[i].idx = idx;
                }
            }

            if( edge[left].x > edge[right].x )
            {
                left ^= 1;
                right ^= 1;
            }

            x1 = edge[left].x;
            x2 = edge[right].x;

            if( y >= 0 )
            {
                Int32 xx1 = (x1 + delta1) >> XY_SHIFT;
                Int32 xx2 = (x2 + delta2) >> XY_SHIFT;

                if( xx2 >= 0 && xx1 < nWidth )
                {
                    if( xx1 < 0 )
                        xx1 = 0;
                    if( xx2 >= nWidth )
                        xx2 = nWidth - 1;
                    ICV_HLINE( ptr, xx1, xx2, color, pix_size );
                }
            }

            x1 += edge[left].dx;
            x2 += edge[right].dx;

            edge[left].x = x1;
            edge[right].x = x2;
            ptr += step;
        }
        while( ++y <= ymax );
        return 0;
    }

