    Int8 MyDottedLine(Mat *matIn_Out,L_Point32 st,L_Point32 ed,Int32 color,	Int32 dis, dottedtype Mod,linetype connectivity,Int32 thickness)
    {
        switch (Mod)
        {
            case CV_DOTTEDLINE_LINE:
            {
                float n = dis; //线长度
                float w = ed.x - st.x, h = ed.y - st.y;
                float l = sqrtf(w * w + h * h);
                // 矫正线长度，使线个数为奇数
                int m = l / n;
                RotatedRect boxst,boxed;

                m = m % 2 ? m : m + 1;
                n = l / m;
                boxst.angle=0;
                boxst.center.x=st.x;
                boxst.center.y=st.y;
                boxst.size.width=1;
                boxst.size.height=1;
                boxed.angle=0;
                boxed.center.x=ed.x;
                boxed.center.y=ed.y;
                boxed.size.width=1;
                boxed.size.height=1;

                MyEllipse(matIn_Out, boxst, color,connectivity, thickness);
                MyEllipse(matIn_Out, boxed, color,connectivity, thickness);

                if (st.y == ed.y) //水平线：y = m
                {
                    float x1 = MIN(st.x, ed.x);
                    float x2 = MAX(st.x, ed.x);
                    float x,n1;
                    L_Point32 m_st,m_ed;
                    for (x = x1, n1 = 2 * n; x < x2; x = x + n1)
                    {
                        m_st.x=x+0.5;
                        m_st.y=st.y;
                        m_ed.x=x+n+0.5;
                        m_ed.y=st.y;
                        MyLine(matIn_Out,m_st,m_ed,color,connectivity,thickness);
                    }
                }
                else if (st.x == ed.x) //垂直线, x = m
                {
                    float y1 = MIN(st.y, ed.y);
                    float y2 = MAX(st.y, ed.y);
                    float y,n1;
                    L_Point32 m_st,m_ed;
                    for (y = y1, n1 = 2 * n; y < y2; y = y + n1)
                    {
                        m_st.x=st.x;
                        m_st.y=y+0.5;
                        m_ed.x=st.x;
                        m_ed.y=y+n+0.5;
                        MyLine(matIn_Out,m_st,m_ed,color,connectivity,thickness);
                    }
                }
                else // 倾斜线，与x轴、y轴都不垂直或平行
                {
                    // 直线方程的两点式：(y-y1)/(y2-y1)=(x-x1)/(x2-x1) -> y = (y2-y1)*(x-x1)/(x2-x1)+y1
                    float n1 = n * abs(w) / l;
                    float k = h / w;
                    float x1 = MIN(st.x, ed.x);
                    float x2 = MAX(st.x, ed.x);
                    float x,n2;
                    L_Point32 m_st,m_ed;
                    for (x = x1, n2 = 2 * n1; x < x2; x = x + n2)
                    {
                        m_st.x=x+0.5;
                        m_st.y=k * (x - st.x) + st.y + 0.5;
                        m_ed.x=x + n1 +0.5;
                        m_ed.y=k * (x + n1 - st.x) + st.y+0.5;
                        MyLine(matIn_Out,m_st,m_ed,color,connectivity,thickness);
                    }
                }
            }
            break;
            case CV_DOTTEDLINE_POINT:
            {
                float n = dis;
                float w = ed.x - st.x, h = ed.y - st.y;
                float l = sqrtf(w * w + h * h);
                int m = l / n;
                RotatedRect boxst,boxed;
                RotatedRect box;

                n = l / m;
                boxst.angle=0;
                boxst.center.x=st.x;
                boxst.center.y=st.y;
                boxst.size.width=1;
                boxst.size.height=1;
                boxed.angle=0;
                boxed.center.x=ed.x;
                boxed.center.y=ed.y;
                boxed.size.width=1;
                boxed.size.height=1;
                box.angle=0;
                box.size.width=1;
                box.size.height=1;
                MyEllipse(matIn_Out, boxst, color,connectivity, thickness);
                MyEllipse(matIn_Out, boxed, color,connectivity, thickness);

                if (st.y == ed.y)
                {
                    float x1 = MIN(st.x, ed.x);
                    float x2 = MAX(st.x, ed.x);
                    float x;
                    for (x = x1 + n; x < x2; x = x + n)
                    {
                        box.center.x=x;
                        box.center.y=st.y;
                        MyEllipse(matIn_Out, box, color, connectivity,thickness);
                    }
                }
                else if (st.x == ed.x)
                {
                    float y1 = MIN(st.y, ed.y);
                    float y2 = MAX(st.y, ed.y);
                    float y;
                    for (y = y1 + n; y < y2; y = y + n)
                    {
                        box.center.x=st.x;
                        box.center.y=y;
                        MyEllipse(matIn_Out, box, color, connectivity,thickness);
                    }
                }
                else // 倾斜线，与x轴、y轴都不垂直或平行
                {
                    // 直线方程的两点式：(y-y1)/(y2-y1)=(x-x1)/(x2-x1) -> y = (y2-y1)*(x-x1)/(x2-x1)+y1
                    float m = n * abs(w) / l;
                    float k = h / w;
                    float x1 = MIN(st.x, ed.x);
                    float x2 = MAX(st.x, ed.x);
                    float x;
                    for (x = x1 + m; x < x2; x = x + m)
                    {
                        box.center.x=x;
                        box.center.y=k * (x - st.x) + st.y;
                        MyEllipse(matIn_Out, box, color, connectivity,thickness);
                    }
                }
            }
            break;
            default:
                return 1;
            break;
        }
        return 0;
    }

