    Int8 MyGetLineCirclefocal(L_line lineIn,double centerXIn,double centerYIn,double radiusIn,L_Point32 *focal1,L_Point32 *focal2)
    {
        double x1=lineIn.st.x;
        double y1=lineIn.st.y;
        double x2=lineIn.ed.x;
        double y2=lineIn.ed.y;

        double k;
        double b;
        double aX,bX,cX;
        double m=centerXIn;
        double n=centerYIn;
        double r=radiusIn;
        double result;
        double xArr1,xArr2;
        double yArr1,yArr2;

        if(x1!=x2)
        {
            k = (y1 - y2) / (x1 - x2);
            b = (x1 * y2 - x2 * y1) / (x1 - x2);
            aX = 1 + k * k;
            bX = 2 * k * (b - n) - 2 * m;
            cX = m * m + (b - n) * (b - n) - r * r;
            result = pow(bX, 2) - 4*aX*cX;
            if(result>0)
            {
                xArr1=(-bX + sqrt(result))/(2 * aX);
                yArr1 = k * xArr1 + b;
                xArr2=(-bX - sqrt(result))/(2 * aX);
                yArr2 = k * xArr2 + b;
                if(yArr1<yArr2)
                {
                    focal1->x=xArr1;
                    focal1->y=yArr1;
                    focal2->x=xArr2;
                    focal2->y=yArr2;
                }
                else if(yArr1>yArr2)
                {
                    focal2->x=xArr1;
                    focal2->y=yArr1;
                    focal1->x=xArr2;
                    focal1->y=yArr2;
                }
                else
                {
                    if(xArr1<xArr2)
                    {
                        focal1->x=xArr1;
                        focal1->y=yArr1;
                        focal2->x=xArr2;
                        focal2->y=yArr2;
                    }
                    else
                    {
                        focal2->x=xArr1;
                        focal2->y=yArr1;
                        focal1->x=xArr2;
                        focal1->y=yArr2;
                    }
                }
            }
            else if(result==0)
            {
                xArr1=(-bX / (2 * aX));
                yArr1 = k * xArr1 + b;
                focal1->x=xArr1;
                focal1->y=yArr1;
                focal2->x=xArr1;
                focal2->y=yArr1;
            }
            else
            {
                return 1;
            }
        }
        else
        {
            xArr1=-sqrt(r*r-(y1-n)*(y1-n))+m;
            xArr2=sqrt(r*r-(y1-n)*(y1-n))+m;
            focal1->x=xArr1;
            focal1->y=y1;
            focal2->x=xArr2;
            focal2->y=y1;
        }
        return 0;
    }

    Int8 MyGetCirclefocal(double centerXIn1,double centerYIn1,double radiusIn1,double centerXIn2,double centerYIn2,double radiusIn2,L_Point32 *focal1,L_Point32 *focal2)
    {
        double a1=centerXIn1;
        double a2=centerXIn2;
        double b1=centerYIn1;
        double b2=centerYIn2;
        double r1=radiusIn1;
        double r2=radiusIn2;
        double K=-(2*a1-2*a2);
        double B=-(2*b1-2*b2);
        double C=(r1*r1-r2*r2)-(a1*a1-a2*a2+b1*b1-b2*b2);
        double cX=-(r1*r1-C*C/(K*K)-a1*a1+2*a1*C/K-b1*b1);
        double aX=B*B/(K*K)+1;
        double bX=-(2*C*B/(K*K)-2*a1*B/K+2*b1);
        double result = pow(bX, 2) - 4*aX*cX;
        double xArr1,xArr2;
        double yArr1,yArr2;

        if(result>0)
        {
            yArr1=(-bX+sqrt(result))/(2*aX);
            xArr1=(C-B*yArr1)/K;
            yArr2=(-bX-sqrt(result))/(2*aX);
            xArr2=(C-B*yArr2)/K;
            if(xArr1<xArr2)
            {
                focal1->x=xArr1;
                focal1->y=yArr1;
                focal2->x=xArr2;
                focal2->y=yArr2;
            }
            else if(xArr1>xArr2)
            {
                focal2->x=xArr1;
                focal2->y=yArr1;
                focal1->x=xArr2;
                focal1->y=yArr2;
            }
            else
            {
                if(yArr1<yArr2)
                {
                    focal1->x=xArr1;
                    focal1->y=yArr1;
                    focal2->x=xArr2;
                    focal2->y=yArr2;
                }
                else
                {
                    focal2->x=xArr1;
                    focal2->y=yArr1;
                    focal1->x=xArr2;
                    focal1->y=yArr2;
                }
            }
        }
        else if(result==0)
        {
            yArr1=(-bX/(2*aX));
            xArr1=(C-B*yArr1)/K;
            focal1->x=xArr1;
            focal1->y=yArr1;
            focal2->x=xArr1;
            focal2->y=yArr1;
        }
        else
        {
            return 1;
        }

        return 0;
    }

