    Int8 Myfixdata(Int32 *dataIn_Out,Uint8 *mask,Int32 num)
    {
        Int32 n,m;
        Int32 nST,nED;
        Int32 *u32_data=(Int32*)u8_buffer_x3Temp1;
        Int32 seed=0;
        Uint8 nTemp=0;
        Int32 addnum;
        Int32 add;
        Int32 t;
        Uint8 have=0;

        memset(u32_data,0,num*sizeof(Int32));
        for(n=0;n<num;n++)
        {
            if(mask[n]==1)
            {
                have=1;
                break;
            }
        }
        if(have==0)
        {
            return 1;
        }
        for(n=0;n<num;n++)
        {
            if(mask[n]==0&&nTemp==0)
            {
                nTemp=1;
                u32_data[seed++]=n;
            }
            else if(mask[n]!=0&&nTemp==1)
            {
                nTemp=0;
                u32_data[seed++]=n;
            }
        }
        if(nTemp==1)
        {
            u32_data[seed++]=num;
        }
        for(n=0;n<seed;n=n+2)
        {
            nST=u32_data[n];
            nED=u32_data[n+1];
            addnum=(nED-nST)+1;
            if(nST==0)
            {
                for(m=nST;m<nED;m++)
                {
                    dataIn_Out[m]=dataIn_Out[nED];
                }
            }
            else if(nED==num)
            {
                for(m=nST;m<nED;m++)
                {
                    dataIn_Out[m]=dataIn_Out[nST-1];
                }
            }
            else
            {
                t=1;
                add=((dataIn_Out[nED]-dataIn_Out[nST-1])<<12)/addnum;
                for(m=nST;m<nED;m++)
                {
                    dataIn_Out[m]=dataIn_Out[nST-1]+((add*t)>>12);
                    t++;
                }
            }
        }
        return 0;
    }

