    Int8 MychangeI32toF32data(Int32 *dataIn,float *dataOut,Int32 num)
    {
        Int32 head;
        Int32 headi=0;
        Int32 taili=0;
        Int32 i,n;
        Int8 dizeng=-1;

        while(headi<num)
        {
            head=dataIn[headi];
            for(i=headi;i<num;i++)
            {
                if(head!=dataIn[i])
                {
                    taili=i-1;
                    if(head<dataIn[i])
                    {
                        dizeng=1;
                    }
                    else
                    {
                        dizeng=0;
                    }
                    break;
                }
                if(i==num-1)
                {
                    taili=i;
                }
            }
            if(dizeng==1)
            {
                if(taili==headi)
                {      
                    dataOut[headi]=dataIn[headi];
                }
                else
                {
                    float f_st=head-0.5;
                    float f_add=1.0/(taili-headi+1);
                    int t=1;
                    for(n=headi;n<=taili;n++)
                    {
                        dataOut[n]=f_st+f_add*t;
                        t++;
                    }
                }
            }
            else if(dizeng==0)
            {
                if(taili==headi)
                {
                    dataOut[headi]=dataIn[headi];
                }
                else
                {
                    float f_st=head+0.5;
                    float f_add=-1.0/(taili-headi+1);
                    int t=1;
                    for(n=headi;n<=taili;n++)
                    {
                        dataOut[n]=f_st+f_add*t;
                        t++;
                    }
                }
            }
            headi=taili+1;
        }
        if(dizeng==-1)
        {
            for(i=0;i<num;i++)
            {
                dataOut[i]=dataIn[i];
            }
        }

        return 0;
    }

